import React, { useState, useEffect } from "react";

function PlayerHand({ socket, gameId, username }) {
  const [playerCards, setPlayerCards] = useState([]);
  const [selectedCard, setSelectedCard] = useState("");

  useEffect(() => {
    socket.on("player_hand", (data) => {
      setPlayerCards(data);
    });

    return () => {
      socket.off("player_hand");
    };
  }, [socket]);

  const playCard = () => {
    if (selectedCard.trim()) {
      socket.emit("play_card", { gameId, selectedCard });
      setSelectedCard("");
    }
  };

  const passTurn = () => {
    socket.emit("play_card", { gameId, selectedCard: "pass" });
  };

  return (
    <div>
      <h3>Your Hand</h3>
      <p>{playerCards.join(" ")}</p>
      <input type="text" placeholder="Enter card" value={selectedCard} onChange={(e) => setSelectedCard(e.target.value)} />
      <button onClick={playCard}>Play Card</button>
      <button onClick={passTurn}>Pass</button>
    </div>
  );
}

export default PlayerHand;
