import React from 'react';

const PlayingCard = ({ card, selected, onClick, disabled }) => {
  // Helper function to determine card color
  const getColor = (card) => {
    const suit = card.slice(-1);
    return suit === 'H' || suit === 'D' ? 'text-red-600' : 'text-black';
  };

  // Helper function to get suit symbol
  const getSuitSymbol = (card) => {
    const suit = card.slice(-1);
    switch (suit) {
      case 'H': return '♥';
      case 'D': return '♦';
      case 'C': return '♣';
      case 'S': return '♠';
      default: return suit;
    }
  };

  return (
    <div 
      onClick={() => !disabled && onClick(card)}
      className={`
        relative w-16 h-24 m-1 rounded-lg cursor-pointer
        ${disabled ? 'opacity-50 cursor-not-allowed' : 'hover:shadow-lg hover:-translate-y-1'}
        ${selected ? 'border-2 border-blue-500 -translate-y-2' : 'border border-gray-300'}
        transition-all duration-200
        bg-white
      `}
    >
      <div className={`absolute top-1 left-2 ${getColor(card)}`}>
        {card.slice(0, -1)}
      </div>
      <div className={`absolute bottom-1 right-2 ${getColor(card)}`}>
        {getSuitSymbol(card)}
      </div>
    </div>
  );
};

export default PlayingCard;
