import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Chat from "./Chat";
import PlayerHand from "./PlayerHand";
import GameBoard from "./GameBoard";

function GameRoom({ socket, username }) {
  const location = useLocation();
  const room = location.state?.room || "";
  const [roomSize, setRoomSize] = useState(location.state?.roomSize || 0);
  const [gameId, setGameId] = useState("");
  const [gameStarted, setGameStarted] = useState(false);

  useEffect(() => {
    socket.on("room_info_update", ({ roomSize, gameId }) => {
      setRoomSize(roomSize);
      setGameId(gameId);
    });

    socket.on("begin_game", () => {
      setGameStarted(true);
    });

    return () => {
      socket.off("room_info_update");
      socket.off("begin_game");
    };
  }, [socket]);

  return (
    <div>
      <h2>Game Room: {room}</h2>
      <h3>Players in Room: {roomSize}</h3>
      {!gameStarted && <button onClick={() => socket.emit("start_game", { num_players: roomSize, room })}>Start Game</button>}
      {gameStarted && <GameBoard socket={socket} gameId={gameId} username={username} />}
      <Chat socket={socket} room={room} username={username} />
    </div>
  );
}

export default GameRoom;
