import React, { useState, useEffect } from 'react';
import GameRoom from './GameLogic';

function House({ socket }) {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [roomSize, setRoomSize] = useState(0);
  const [gameId, setGameId] = useState("")

  // Join Room
  const joinRoom = () => {
    if (username !== "" && room !== "") {
      socket.emit("join_room", { username, room}, (success, updatedSize, id) => {
        if (success) {
          console.log(`${id} Successfully joined room: ${room}`);
          setRoomSize(updatedSize); // Update room size immediately
        } else {
          console.log(`Failed to join room: ${room}`);
        }
      });
    }
  };

  // Handle Socket Events
  useEffect(() => {
    socket.on("room_info_update", ({ roomSize, gameId }) => {
      setRoomSize(roomSize);
      setGameId(gameId);
    });

    socket.on("invalid_move", (data) => {
      alert(data.message);
    });

    return () => {
      socket.off("room_info_update");
      socket.off("invalid_move");
    };
  }, [socket]);

  return (
    <div>
      <h3>House Component</h3>

      <input
        type="text"
        placeholder="Username"
        onChange={(event) => setUsername(event.target.value)}
      />
      <input
        type="text"
        placeholder="Room ID"
        onChange={(event) => setRoom(event.target.value)}
      />
      <button onClick={joinRoom}>Join Room</button>

      <h3>Users in Room: {roomSize}</h3>

      <div style={{ marginBottom: "20px" }}>
        <GameLogic username={username} socket={socket} room={room} roomSize={roomSize} newGameId={gameId} />
      </div>
    </div>
  );
}

export default House;
