import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

function Home() {
  // Create an array of card suits for the pattern
  const cardSuits = ['♠', '♣', '♦', '♥'];
  const cardPatternRef = useRef(null);

  // Generate card pattern items in a checkered pattern (12x12 grid)
  const generateCardPattern = () => {
    const items = [];
    const rows = 12;
    const cols = 12;

    for (let row = 0; row < rows; row++) {
      for (let col = 0; col < cols; col++) {
        // Create a checkered pattern by alternating suits
        // For even rows, use one pattern; for odd rows, offset the pattern
        const suitIndex = (row % 2 === 0) 
          ? (col % 4) // Even rows: ♠, ♣, ♦, ♥, ♠, ♣, ...
          : ((col + 2) % 4); // Odd rows: ♦, ♥, ♠, ♣, ♦, ♥, ...

        items.push(
          <div 
            key={`${row}-${col}`} 
            className="card-pattern-item parallax"
            data-depth={((row + col) % 5) / 10 + 0.1} // Varying depth for parallax effect
          >
            {cardSuits[suitIndex]}
          </div>
        );
      }
    }
    return items;
  };

  // Create parallax effect on mouse move
  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!cardPatternRef.current) return;

      const patternItems = cardPatternRef.current.querySelectorAll('.parallax');
      const containerRect = cardPatternRef.current.getBoundingClientRect();

      const centerX = containerRect.width / 2;
      const centerY = containerRect.height / 2;

      const mouseX = e.clientX - containerRect.left;
      const mouseY = e.clientY - containerRect.top;

      const offsetX = (mouseX - centerX) / 25;
      const offsetY = (mouseY - centerY) / 25;

      patternItems.forEach((item) => {
        const depth = parseFloat(item.getAttribute('data-depth'));
        const itemX = offsetX * depth;
        const itemY = offsetY * depth;

        item.style.transform = `translateX(${itemX}px) translateY(${itemY}px)`;
      });
    };

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  // Create card examples for different play types
  const renderCardExample = (type) => {
    switch(type) {
      case 'single':
        return (
          <div className="card-example">
            <div className="card black">A♠</div>
          </div>
        );
      case 'pair':
        return (
          <div className="card-example">
            <div className="card black">7♠</div>
            <div className="card red">7♦</div>
          </div>
        );
      case 'triple':
        return (
          <div className="card-example">
            <div className="card black">J♣</div>
            <div className="card red">J♥</div>
            <div className="card black">J♠</div>
          </div>
        );
      case 'straight':
        return (
          <div className="card-example">
            <div className="card black">5♠</div>
            <div className="card red">6♥</div>
            <div className="card black">7♣</div>
            <div className="card red">8♦</div>
            <div className="card black">9♠</div>
          </div>
        );
      case 'double-straight':
        return (
          <div className="card-example">
            <div className="card black">4♠</div>
            <div className="card red">4♦</div>
            <div className="card black">5♣</div>
            <div className="card red">5♥</div>
          </div>
        );
      case 'quad':
        return (
          <div className="card-example">
            <div className="card black">Q♠</div>
            <div className="card red">Q♥</div>
            <div className="card black">Q♣</div>
            <div className="card red">Q♦</div>
          </div>
        );
      default:
        return null;
    }
  };

  // Create card divider with suits
  const renderCardDivider = () => {
    return (
      <div className="card-divider">
        <div className="card-divider-item" data-suit="♠"></div>
        <div className="card-divider-item" data-suit="♣"></div>
        <div className="card-divider-item" data-suit="♦"></div>
        <div className="card-divider-item" data-suit="♥"></div>
        <div className="card-divider-item" data-suit="♠"></div>
      </div>
    );
  };

  return (
    <div className="home-container">
      <div className="card-pattern" ref={cardPatternRef}>
        {generateCardPattern()}
      </div>
      <div className="home-content">
        <div className="home-header">
          <h1>Welcome to Thirteen</h1>
          <p>Our version of the classic Vietnamese card game Tiến Lên (Thirteen)</p>
          <div className="card-suits">
            <span className="suit-icon">♠</span>
            <span className="suit-icon">♣</span>
            <span className="suit-icon">♦</span>
            <span className="suit-icon">♥</span>
          </div>
        </div>

        <div className="instructions">
          <div className="instructions-section">
            <div className="corner-top-right"></div>
            <div className="corner-bottom-left"></div>
            <h3>Game Overview</h3>
            <p>
              Thirteen is a multiplayer card game where players aim to be the first to get rid of all their cards.
              The game supports 2-4 players and uses a standard 52-card deck.
            </p>
          </div>

          {renderCardDivider()}

          <div className="instructions-section">
            <div className="corner-top-right"></div>
            <div className="corner-bottom-left"></div>
            <h3>Card Rankings</h3>
            <p>Cards are ranked from lowest to highest:</p>
            <ul>
              <li data-suit="♠">3, 4, 5, 6, 7, 8, 9, 10, Jack, Queen, King, Ace, 2</li>
            </ul>
            <p>Suits are ranked:</p>
            <ul>
              <li data-suit="♠">Spades (lowest)</li>
              <li data-suit="♣">Clubs</li>
              <li data-suit="♦" data-suit-color="red">Diamonds</li>
              <li data-suit="♥" data-suit-color="red">Hearts (highest)</li>
            </ul>
          </div>

          {renderCardDivider()}

          <div className="instructions-section">
            <div className="corner-top-right"></div>
            <div className="corner-bottom-left"></div>
            <h3>Valid Plays</h3>
            <ul>
              <li data-suit="♠">
                Single cards: Play any single card
                {renderCardExample('single')}
              </li>
              <li data-suit="♣">
                Pairs: Two cards of the same rank
                {renderCardExample('pair')}
              </li>
              <li data-suit="♦" data-suit-color="red">
                Triples: Three cards of the same rank
                {renderCardExample('triple')}
              </li>
              <li data-suit="♥" data-suit-color="red">
                Quads: Four cards of the same rank
                {renderCardExample('quad')}
              </li>
              <li data-suit="♠">
                Straights: Five or more consecutive cards
                {renderCardExample('straight')}
              </li>
              <li data-suit="♣">
                Double straights: Two or more consecutive pairs
                {renderCardExample('double-straight')}
              </li>
            </ul>
          </div>

          {renderCardDivider()}

          <div className="instructions-section">
            <div className="corner-top-right"></div>
            <div className="corner-bottom-left"></div>
            <h3>Game Rules</h3>
            <ul>
              <li data-suit="♠">The player with the 3 of Spades starts the first round</li>
              <li data-suit="♣">You must play a higher card or combination than the previous play</li>
              <li data-suit="♦" data-suit-color="red">You can only play the same type of combination as the current one</li>
              <li data-suit="♥" data-suit-color="red">If you cannot play or choose to pass, you skip your turn</li>
              <li data-suit="♠">When all players pass, the last player to play starts a new round</li>
              <li data-suit="♣">The first player to play all their cards wins</li>
            </ul>
          </div>

          {renderCardDivider()}

          <div className="instructions-section">
            <div className="corner-top-right"></div>
            <div className="corner-bottom-left"></div>
            <h3>Special Combinations</h3>
            <ul>
              <li data-suit="♠">
                Four of a kind: Beats any single card, pair, or triple
              </li>
              <li data-suit="♣">
                Straight flush: Five consecutive cards of the same suit
              </li>
              <li data-suit="♦" data-suit-color="red">
                Dragon: A straight from 3 to 2 (twelve cards)
              </li>
              <li data-suit="♥" data-suit-color="red">
                Four 2's: The highest combination in the game
              </li>
            </ul>
          </div>

          <div className="play-button-container">
            <div className="card-deck">
              <div className="deck-card"></div>
              <div className="deck-card"></div>
              <div className="deck-card"></div>
              <div className="deck-card"></div>
              <div className="deck-card"></div>
            </div>
            <Link to="/game" className="play-button">PLAY NOW</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
