import React, { useState, useEffect } from "react";
import PlayerHand from "./PlayerHand";

function GameBoard({ socket, gameId, username }) {
  const [currentTurn, setCurrentTurn] = useState("");
  const [lastPlayedCard, setLastPlayedCard] = useState("");

  useEffect(() => {
    socket.on("game_state_update", (data) => {
      setCurrentTurn(data.currentTurn);
      setLastPlayedCard(data.lastPlayedCard);
    });

    return () => {
      socket.off("game_state_update");
    };
  }, [socket]);

  return (
    <div>
      <h2>{currentTurn}'s turn</h2>
      <h3>Last Played Card: {lastPlayedCard || "Play any card"}</h3>
      <PlayerHand socket={socket} gameId={gameId} username={username} />
    </div>
  );
}

export default GameBoard;
