import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:5001");

function GameLogic({ room, roomSize, newGameId }) {
  const [playerCards, setPlayerCards] = useState([]);
  const [gameId, setGameId] = useState(newGameId);

  // Start Game: Calls Python backend
  const startGame = () => {
    if (roomSize >= 1 && roomSize <= 4) {
      console.log("Requesting game start via WebSocket...");

      socket.emit("start_game", { num_players: roomSize, room });

    } else {
      console.log("Wrong number of players");
    }
  };


  // useEffect(() => {
  //   console.log("Setting up game_state_update listener");
  //   socket.on("game_state_update", (data) => {
  //     console.log("Game state updated:", data);
  //     setGameId(data.game_id);
  //     setPlayerCards(data.player_cards);
  //   });

  //   if (newGameId) {
  //     setGameId(newGameId);
  //   }

  //   return () => {
  //     console.log("Cleaning up game_state_update listener");
  //     socket.off("game_state_update"); // Cleanup
  //   };
  // }, [newGameId]);
  useEffect(() => {
    console.log("Setting up game_state_update listener");

    const handleGameUpdate = (data) => {
      console.log("Game state updated:", data);
      setGameId(data.game_id);
      setPlayerCards([...data.player_cards]); // ðŸ”´ Forces a React re-render
    };

    socket.off("game_state_update").on("game_state_update", handleGameUpdate);

    return () => {
      console.log("Cleaning up game_state_update listener");
      socket.off("game_state_update", handleGameUpdate);
    };
  }, []);

  return (
    <div>
      <h3>Game Logic</h3>

      <button onClick={startGame}>Start Game</button>

      {gameId && (
        <div>
          <h3>Game ID: {gameId}</h3>
        </div>
      )}

      {playerCards.length > 0 &&
        playerCards.map((hand, index) => (
          <div key={index}>
            <h4>Player {index + 1} Hand:</h4>
            <p>{hand.join(", ")}</p>
          </div>
        ))}
    </div>
  );
}

export default GameLogic;
