import React, { useState } from "react";

function GameLogic() {
  const [numPlayers, setNumPlayers] = useState(0);
  const [playerCards, setPlayerCards] = useState([]);
  const [gameId, setGameId] = useState(null);

  // Start Game: Calls Python backend
  const startGame = async () => {
        try {
          const response = await fetch("http://localhost:5000/start_game", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ num_players: numPlayers }),
          });

          if (!response.ok) throw new Error("Failed to start game");

          const data = await response.json();
          setGameId(data.game_id);
          setPlayerCards(data.player_cards);
        } catch (error) {
          console.error("Error:", error);
        }
  };

  // Fetch Game State from Python backend
  const fetchGameState = async () => {
    if (!gameId) return;
    try {
      const response = await fetch(`http://localhost:5000/game_state/${gameId}`);
      if (!response.ok) throw new Error("Game state not found");

      const data = await response.json();
      setPlayerCards(data.player_cards);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div>
      <h3>Game Logic</h3>

      {/* <label>Number of Players: {numPlayers}</label> */}

      <button onClick={startGame}>Start Game</button>

      {gameId && (
        <div>
          <h3>Game ID: {gameId}</h3>
          <button onClick={fetchGameState}>Refresh Game State</button>
        </div>
      )}

      {playerCards.length > 0 &&
        playerCards.map((hand, index) => (
          <div key={index}>
            <h4>Player {index + 1} Hand:</h4>
            <p>{hand.join(", ")}</p>
          </div>
        ))}
    </div>
  );
}

export default GameLogic;
