import React, { useState, useEffect } from "react";
import "./Chat.css";

function Chat({ socket, room, username, isOpen, onClose, messages, setMessages }) {
  const [message, setMessage] = useState("");

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit("send_message", { room, username, message });
      setMessages((prev) => [...prev, { username, message }]); // Save message in GameRoom
      setMessage("");
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="chat-modal-overlay">
      <div className="chat-modal">
        <div className="chat-header">
          <h3>Game Chat</h3>
          <button className="close-button" onClick={onClose}>Ã—</button>
        </div>

        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.username === username ? 'sent' : 'received'}`}>
              <span className="username">{msg.username}</span>
              <p className="message-content">{msg.message}</p>
            </div>
          ))}
        </div>

        <div className="chat-input">
          <input
            type="text"
            placeholder="Type a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={handleKeyPress}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
}


export default Chat;
