import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Lobby({ socket, setUsername }) {
  const [room, setRoom] = useState("");
  const [localUsername, setLocalUsername] = useState("");
  const navigate = useNavigate();

  const joinRoom = () => {
    if (localUsername.trim() && room.trim()) {
      setUsername(localUsername);
      socket.emit("join_room", { username: localUsername, room }, (success, roomSize, id) => {
        if (success) {
          console.log(`Successfully joined room: ${room}`);
          navigate("/game", { state: { room, roomSize } });
        } else {
          alert("Failed to join room.");
        }
      });
    } else {
      alert("Enter a valid username and room.");
    }
  };

  return (
    <div>
      <h2>Join a Room</h2>
      <input type="text" placeholder="Username" onChange={(e) => setLocalUsername(e.target.value)} />
      <input type="text" placeholder="Room ID" onChange={(e) => setRoom(e.target.value)} />
      <button onClick={joinRoom}>Join Room</button>
    </div>
  );
}

export default Lobby;
