import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import "./Lobby.css";

function Lobby({ socket }) {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");
  const [roomSize, setRoomSize] = useState(0);
  const [gameId, setGameId] = useState("");
  const navigate = useNavigate();

  // Join Room
  const joinRoom = () => {
    if (username !== "" && room !== "") {
      socket.emit("join_room", { username, room }, (success, updatedSize, id) => {
        if (success) {
          console.log(`${id} Successfully joined room: ${room}`);
          setGameId(id);
        } else {
          alert(`Failed to join room: ${room}`);
        }
      });
    }
  };

  // Start Game
  const startGame = () => {
    if (roomSize > 1 && roomSize <= 4) {
      socket.emit("start_game", { num_players: roomSize, room });
    } else {
      alert("Invalid number of players.");
    }
  };

  // Handle Socket Events
  useEffect(() => {
    socket.on("room_info_update", ({ roomSize, gameId }) => {
      setRoomSize(roomSize);
      setGameId(gameId);
    });

    socket.on("invalid_move", (data) => {
      alert(data.message);
    });

    socket.on("begin_game", () => {
      console.log("Game begin");
      console.log("sending this data: ",  { username, room, roomSize, gameId });
      navigate("/game", { state: { username, room, roomSize, gameId } });
    });

    return () => {
      socket.off("room_info_update");
      socket.off("invalid_move");
      socket.off("begin_game");
    };
  }, [socket, username, room, roomSize, gameId, navigate]);

  return (
    <div className="min-h-screen bg-amber-900/90 flex items-center justify-center p-4">
      <div className="w-full max-w-md bg-amber-50 rounded-lg shadow-2xl p-8 relative">
        {/* Card game logo */}
        <div className="absolute -top-12 left-1/2 -translate-x-1/2 w-24 h-24 bg-amber-800 rounded-full flex items-center justify-center">
          <div className="text-white text-4xl">♠️</div>
        </div>

        {/* Main content */}
        <div className="mt-8 space-y-6">
          <h1 className="text-2xl font-bold text-amber-900 text-center mb-8">Join Room</h1>

          <div className="space-y-4">
            <input
              type="text"
              placeholder="Username"
              className="w-full px-4 py-3 rounded-lg bg-amber-100 border border-amber-300 focus:outline-none focus:ring-2 focus:ring-amber-500 transition"
              onChange={(e) => setUsername(e.target.value)}
            />

            <input
              type="text"
              placeholder="Room ID"
              className="w-full px-4 py-3 rounded-lg bg-amber-100 border border-amber-300 focus:outline-none focus:ring-2 focus:ring-amber-500 transition"
              onChange={(e) => setRoom(e.target.value)}
            />
          </div>

          <button
            onClick={joinRoom}
            className="w-full bg-amber-800 text-white py-3 rounded-lg hover:bg-amber-700 transition font-medium"
          >
            JOIN ROOM
          </button>

          {roomSize > 0 && (
            <div className="space-y-4">
              <div className="text-center text-amber-900">
                Players in Room: {roomSize}/4
              </div>

              <button
                onClick={startGame}
                className="w-full bg-green-700 text-white py-3 rounded-lg hover:bg-green-600 transition font-medium"
              >
                START GAME
              </button>
            </div>
          )}
        </div>

        {/* Decorative elements */}
        <div className="absolute -left-4 -right-4 -bottom-4 h-2 bg-amber-900 rounded-b-lg"></div>
        <div className="absolute -left-2 -right-2 -bottom-2 h-2 bg-amber-800 rounded-b-lg"></div>
      </div>
    </div>
  );
}

export default Lobby;
