import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer } from "react-toastify";
import './Lobby.css';

function Lobby({ socket }) {
  const [formData, setFormData] = useState({
    username: "",
    room: ""
  });
  const [roomState, setRoomState] = useState({
    size: 0,
    gameId: ""
  });
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value.trim()
    }));
  };

  const joinRoom = () => {
    if (formData.username && formData.room) {
      socket.emit("join_room", { 
        username: formData.username, 
        room: formData.room 
      }, handleJoinResponse);
    } else {
      toast.warn("Please fill in all fields", {
        position: "top-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        draggable: true,
        pauseOnHover: false,
        theme: "light",
      });
    }
  };

  const handleJoinResponse = (success, updatedSize, id) => {
    if (success) {
      setRoomState(prev => ({ ...prev, gameId: id }));
      toast.success(`Successfully joined room: ${formData.room}`, {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        draggable: true,
        pauseOnHover: false,
        theme: "light",
      });
    } else {
      toast.error("Failed to join room", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        draggable: true,
        pauseOnHover: false,
        theme: "light",
      });
    }
  };

  const startGame = () => {
    if (roomState.size > 1 && roomState.size <= 4) {
      socket.emit("start_game", { 
        num_players: roomState.size, 
        room: formData.room 
      });
    } else {
      toast.error("Need 2-4 players to start the game", {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        draggable: true,
        pauseOnHover: false,
        theme: "light",
      });
    }
  };

  useEffect(() => {
    const handleRoomUpdate = ({ roomSize, gameId }) => {
      setRoomState({ size: roomSize, gameId });
    };

    const handleGameStart = () => {
      navigate("/game", { 
        state: { 
          username: formData.username, 
          room: formData.room, 
          roomSize: roomState.size, 
          gameId: roomState.gameId 
        } 
      });
    };

    const handleInvalidMove = (data) => {
      toast.error(data.message, {
        position: "top-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        draggable: true,
        pauseOnHover: false,
        theme: "light",
      });
    };

    socket.on("room_info_update", handleRoomUpdate);
    socket.on("begin_game", handleGameStart);
    socket.on("invalid_move", handleInvalidMove);

    return () => {
      socket.off("room_info_update");
      socket.off("begin_game");
      socket.off("invalid_move");
    };
  }, [socket, formData, roomState, navigate]);

  return (
    <div className="lobby-container">
      <ToastContainer />
      <div className="lobby-content">
        <div className="lobby-card">
          <div className="lobby-header">
            <h1>Thirteen</h1>
            <p className="subtitle">Card Game</p>
          </div>

          <div className="input-section">
            <div className="input-group">
              <input
                type="text"
                name="username"
                className="input-field"
                placeholder="Username"
                value={formData.username}
                onChange={handleInputChange}
                maxLength={15}
              />
            </div>
            <div className="input-group">
              <input
                type="text"
                name="room"
                className="input-field"
                placeholder="Room ID"
                value={formData.room}
                onChange={handleInputChange}
                maxLength={10}
              />
            </div>
          </div>

          <button 
            className="join-button"
            onClick={joinRoom}
            disabled={!formData.username || !formData.room}
          >
            Join Room
          </button>

          {roomState.size > 0 && (
            <div className="game-status">
              <div className="player-counter">
                <span className="player-count">Players: {roomState.size}/4</span>
                <div className="player-slots">
                  {[...Array(4)].map((_, i) => (
                    <div 
                      key={i} 
                      className={`player-slot ${i < roomState.size ? 'filled' : ''}`}
                    />
                  ))}
                </div>
              </div>
              <button 
                className="start-button"
                onClick={startGame}
                disabled={roomState.size < 2 || roomState.size > 4}
              >
                Start Game
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Lobby;
